sourcesContent:
/* 
  UTILS 
 */
 import _ from 'lodash';
 import moment from 'moment';

/*

  Types from api:

      TEAM: {
        code: string
        country: string
        goals: number
      },
      EVENT: {
        id: number,
        player: string,
        time: string,
        type_of_event: string
      }



  */

const fetchData = async (url) => {
  const response = await fetch(url, {
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    }
  });

  const data = await response.json()
  return data;
};

const getMatchSring = (match) => {
  /*
    MATCH: {
      away_team: TEAM,
      away_team_events: EVENT,
      datetime: datetime,
      fifa_id: "300353632"
      home_team: TEAM
      home_team_events: EVENT
      last_event_update_at: ?
      last_score_update_at: ?
      location: string
      status: 'future' | 'in progress' | 'full-time'
      time: string | null
      venue: string
      winner: string | null
      winner_code: string | null
    }
  */
  if (stats === 'future') {
    const time = moment(`${match.datetime}`).format('LT');
    return `${time}\t\t${match.away_team.name} at ${match.home_team.name}`;
  } else if (stats === 'in progress') {
    return `${match.time} ${match.away_team.name}: ${match.away_team.goals} v ${match.home_team.name}: ${match.home_team.goals}`;
  }
  return `Final ${match.away_team.name}: ${match.away_team.goals}  ${match.home_team.name}: ${match.home_team.goals}`;
};

// 
// TESTS / FIX strings
// 
const getTeamStandingString = (team) => {
  return `${team.country}\t\t\t${team.points}\t\t\t${team.goal_differential}`
};

const getGroupStandings = (group) => {
  let lines = '';
  _.forEach(group, (team) => {
    lines = `${lines}${getTeamStandingString(team.team)}\n`;
  });
  return lines;
};

export const getTodaysMatches = async () => {
  /*
    [
      MATCH,
      ...
    ]
  */
  const matches = await fetchData('http://worldcup.sfg.io/matches/today');
  let lines = '';  
  _.forEach(matches, (match) => {
    lines = `${lines}${getMatchSring(match)}\n`
  });
  return lines;
};

export const stats = {
    triggers: 0,
    convos: 0,
};

export const getAllStandings = async () => {
  /*
    [
      {
        group: {
          id: number,
          letter: string,
          teams: [
            {
              team: {
                country: string,
                fifa_code: string,
                goal_differential: number,
                id: number,
                points: string
              }
            },
            ...
          ] 
        }
      },
      ...

    ]
  */
  let lines = '';
  const standings = await fetchData('http://worldcup.sfg.io/teams/group_results');
  _.forEach(standings, (group) => {
    lines = `${lines}Group ${_.get(group, 'group.letter', '')}\n${getGroupStandings(group.group)}\n`;
  });
  return lines;
};

export const formatUptime = (uptime) => {
  let unit = 'second';
  if (uptime > 60) {
    uptime = uptime / 60;
    unit = 'minute';
  }
  if (uptime > 60) {
    uptime = uptime / 60;
    unit = 'hour';
  }
  if (uptime != 1) {
    unit = unit + 's';
  }
  uptime = parseInt(uptime) + ' ' + unit;
  return uptime;
};

