                var person = '<@' + message.user + '>';
                if (message.channel[0] == 'D') {
                    person = 'You';
                }

                reply.attachments.push({
                    text: person + ' said, ' + message.actions[0].value
                });

                bot.replyInteractive(message, reply);
            }
        }

        next();
    });
